// n8n Code node (JavaScript)
// Soluciona: "Unexpected non-whitespace character after JSON..."
// Extrayendo el PRIMER JSON completo (objeto o array) por balanceo de llaves/corchetes.

function getRaw(item) {
  const j = item.json ?? {};
  return j.output ?? j.text ?? j.message ?? j.content ?? j.data ?? j;
}

function extractFirstJsonValue(text) {
  if (typeof text !== "string") text = String(text);

  // Si viene dentro de ```json ... ``` o ``` ... ```, primero intenta sacar el contenido del fence
  const fence = text.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
  if (fence?.[1]) text = fence[1];

  // Busca inicio de JSON: { o [
  const startObj = text.indexOf("{");
  const startArr = text.indexOf("[");
  let start = -1;

  if (startObj === -1) start = startArr;
  else if (startArr === -1) start = startObj;
  else start = Math.min(startObj, startArr);

  if (start === -1) return null;

  // Scanner para encontrar el final exacto del JSON (balanceando y respetando strings)
  let inString = false;
  let escape = false;
  let depth = 0;
  let opener = text[start];
  let closer = opener === "{" ? "}" : "]";

  for (let i = start; i < text.length; i++) {
    const ch = text[i];

    if (inString) {
      if (escape) {
        escape = false;
      } else if (ch === "\\") {
        escape = true;
      } else if (ch === '"') {
        inString = false;
      }
      continue;
    }

    if (ch === '"') {
      inString = true;
      continue;
    }

    if (ch === opener) depth++;
    else if (ch === closer) depth--;

    if (depth === 0) {
      return text.slice(start, i + 1).trim();
    }
  }

  // Si no cerró, no es JSON completo
  return null;
}

function safeParseJson(text) {
  // Limpieza ligera por si vienen comillas “ ”
  const cleaned = text.replace(/[“”]/g, '"').replace(/[‘’]/g, "'");
  return JSON.parse(cleaned);
}

// --- MAIN ---
const out = [];

for (const item of items) {
  const raw = getRaw(item);

  // Si ya es objeto/array, pásalo directo
  if (raw && typeof raw === "object") {
    out.push({ json: raw });
    continue;
  }

  const txt = String(raw);
  const jsonStr = extractFirstJsonValue(txt);

  if (!jsonStr) {
    throw new Error(
      "No encontré un JSON completo en el texto. Primeros 300 chars:\n" +
      txt.slice(0, 300)
    );
  }

  const parsed = safeParseJson(jsonStr);
  out.push({ json: parsed });
}

return out;
