// Intenta encontrar el texto del modelo en distintos campos comunes
const raw =
  $json.text ??
  $json.message ??
  $json.content ??
  $json.output ??
  $json.data ??
  $json;

// Si no es string, lo devolvemos tal cual
if (typeof raw !== "string") {
  return [{ json: raw }];
}

let cleaned = raw.trim();

// Eliminar bloques ```json ``` si existen
cleaned = cleaned.replace(/```json/g, "");
cleaned = cleaned.replace(/```/g, "");

// Intentar extraer solo el bloque JSON (desde el primer { hasta el último })
const firstBrace = cleaned.indexOf("{");
const lastBrace = cleaned.lastIndexOf("}");

if (firstBrace === -1 || lastBrace === -1) {
  throw new Error("No se encontró un JSON válido en la respuesta del modelo.");
}

cleaned = cleaned.substring(firstBrace, lastBrace + 1);

let parsed;

try {
  parsed = JSON.parse(cleaned);
} catch (err) {
  throw new Error(
    "El modelo devolvió algo que no es JSON válido. Primeros 500 chars:\n" +
      cleaned.slice(0, 500)
  );
}

// Devolvemos el JSON limpio
return [{ json: parsed }];
